import React from 'react';

const WhyHire = ({ isVisible }) => {
  const reasons = [
    {
      title: "End-to-End ML Expertise",
      description: "From data preprocessing to model deployment, I can handle the entire machine learning lifecycle and ensure smooth delivery to production.",
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.104 0-2 .896-2 2v2H8v-2a4 4 0 118 0v2h-2v-2c0-1.104-.896-2-2-2zM6 12v8h12v-8"></path>
        </svg>
      )
    },
    {
      title: "LLM & NLP Specialization",
      description: "I work extensively with large language models and advanced NLP pipelines—fine-tuning, RAG systems, embeddings, and model optimization for real-world use.",
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6h4M6 12a9 9 0 1118 0 9 9 0 01-18 0z"></path>
        </svg>
      )
    },
    {
      title: "Reliable & Scalable Deployment",
      description: "I build ML solutions that are production-ready, scalable, and well-monitored—using tools like Docker, Kubernetes, and cloud platforms.",
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 12h14M12 5l7 7-7 7"></path>
        </svg>
      )
    }
  ];

  return (
    <section
      id="whyHire"
      className={`section-padding bg-white dark:bg-gray-900 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Why Hire Me</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-16 max-w-3xl mx-auto">
          Here are three reasons why I can add real value to your AI/ML projects
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {reasons.map((reason, index) => (
            <div
              key={index}
              className="card p-8 text-center group hover:scale-[1.02] transition-transform duration-300"
            >
              <div className="bg-primary-100 dark:bg-primary-900/30 text-primary-600 dark:text-primary-400 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-6 group-hover:bg-accent-500 group-hover:text-white transition-colors duration-300">
                {reason.icon}
              </div>
              <h3 className="text-xl font-bold mb-4">{reason.title}</h3>
              <p className="text-gray-600 dark:text-gray-300">{reason.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default WhyHire;
