// src/components/Skills.js
import React from 'react';

const skillCategories = [
  {
    title: 'Core ML & AI',
    skills: [
      { name: 'Python', icon: 'ðŸ' },
      { name: 'TensorFlow', icon: 'ðŸ§ ' },
      { name: 'PyTorch', icon: 'ðŸ”¥' },
      { name: 'Hugging Face', icon: 'ðŸ¤—' },
      { name: 'NLP', icon: 'ðŸ’¬' },
      { name: 'LLM Fine-Tuning', icon: 'âš™ï¸' },
    ]
  },
  {
    title: 'Frameworks & Tools',
    skills: [
      { name: 'LangChain', icon: 'ðŸ”—' },
      { name: 'Docker', icon: 'ðŸ³' },
      { name: 'AWS', icon: 'â˜ï¸' },
      { name: 'SQL', icon: 'ðŸ“Š' },
    ]
  }
];

const Skills = ({ isVisible }) => {
  return (
    <section
      id="skills"
      className={`section-padding bg-white dark:bg-gray-900 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Technical Skills</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-16 max-w-3xl mx-auto">
          Proficiency in modern machine learning technologies and scalable deployment tools
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
          {skillCategories.map((category, index) => (
            <div key={index}>
              <h3 className="text-xl font-semibold mb-6 text-primary-600 dark:text-primary-400">
                {category.title}
              </h3>
              <div className="flex flex-wrap gap-4">
                {category.skills.map((skill, i) => (
                  <div
                    key={i}
                    className="px-4 py-3 bg-gray-50 dark:bg-gray-800 rounded-lg shadow hover:shadow-lg transition-all duration-300 flex items-center gap-2 text-gray-700 dark:text-gray-300 cursor-default"
                  >
                    <span className="text-2xl">{skill.icon}</span>
                    <span className="font-medium">{skill.name}</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
