// src/components/Header.js
import React from 'react';

const Header = ({ darkMode, setDarkMode }) => {
  return (
    <header className="fixed w-full bg-white/90 dark:bg-gray-900/90 backdrop-blur-sm shadow-sm z-50 transition-colors duration-300">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold text-primary-600">[Your Name]</h1>

        <nav className="hidden md:flex space-x-8">
          <a href="#about" className="hover:text-primary-600 transition font-medium">About</a>
          <a href="#skills" className="hover:text-primary-600 transition font-medium">Skills</a>
          <a href="#projects" className="hover:text-primary-600 transition font-medium">Projects</a>
          <a href="#whyHire" className="hover:text-primary-600 transition font-medium">Why Hire</a>
          <a href="#contact" className="hover:text-primary-600 transition font-medium">Contact</a>
        </nav>

        <button
          onClick={() => setDarkMode(!darkMode)}
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 transition-colors duration-300"
          aria-label="Toggle dark mode"
        >
          {darkMode ? (
            <span className="text-yellow-400">‚òÄÔ∏è</span>
          ) : (
            <span className="text-gray-700">üåô</span>
          )}
        </button>
      </div>
    </header>
  );
};

export default Header;
