// src/components/Skills.js
import React from 'react';

const skills = [
  { name: 'Python', level: 95, icon: '🐍' },
  { name: 'TensorFlow', level: 90, icon: '🧠' },
  { name: 'PyTorch', level: 85, icon: '🔥' },
  { name: 'Hugging Face', level: 90, icon: '🤗' },
  { name: 'NLP', level: 88, icon: '💬' },
  { name: 'LLM Fine-Tuning', level: 85, icon: '⚙️' },
  { name: 'LangChain', level: 80, icon: '🔗' },
  { name: 'Docker', level: 75, icon: '🐳' },
  { name: 'AWS', level: 70, icon: '☁️' },
  { name: 'SQL', level: 80, icon: '📊' },
];

const Skills = ({ isVisible }) => {
  return (
    <section
      id="skills"
      className={`section-padding bg-white dark:bg-gray-900 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Technical Skills</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-16 max-w-3xl mx-auto">
          Proficiency in cutting-edge ML technologies and tools
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
          {skills.map((skill, index) => (
            <div
              key={index}
              className="card p-6 text-center group hover:scale-105 transition-transform duration-300"
            >
              <div className="text-3xl mb-4">{skill.icon}</div>
              <h3 className="font-bold text-lg mb-2">{skill.name}</h3>
              <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5 mb-2">
                <div
                  className="bg-gradient-to-r from-primary-500 to-indigo-600 h-2.5 rounded-full"
                  style={{ width: `${skill.level}%` }}
                ></div>
              </div>
              <span className="text-sm text-gray-600 dark:text-gray-400">{skill.level}%</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
