// src/components/Projects.js
import React from "react";
import { motion } from "framer-motion";

const projects = [
  {
    title: "Knowledge Distillation for Intel Image Classification",
    description: "Compressed a VisionEagle-based model into a lightweight CNN, reducing size by 99.6% with minimal accuracy loss.",
    tech: ["PyTorch", "VisionEagle", "CNN", "KD"],
    link: "#"
  },
  {
    title: "RUL Inference Engine",
    description: "Microservice-based Remaining Useful Life prediction system with FastAPI and Streamlit.",
    tech: ["LSTM", "FastAPI", "Streamlit", "Docker"],
    link: "#"
  },
  {
    title: "Predict Calories",
    description: "App predicting calories burned during exercise using XGBoost and microservices.",
    tech: ["XGBoost", "FastAPI", "Streamlit", "Python"],
    link: "#"
  },
  {
    title: "ModernBERT for NLI",
    description: "Fine-tuned ModernBERT for Natural Language Inference using Hugging Face Transformers.",
    tech: ["ModernBERT", "Transformers", "PyTorch"],
    link: "#"
  },
  {
    title: "BERT for NER",
    description: "Fine-tuned BERT on CoNLL-2003 achieving 94% F1 for Named Entity Recognition.",
    tech: ["BERT", "Hugging Face", "Transformers"],
    link: "#"
  },
  {
    title: "Amazon Product Listing Optimizer",
    description: "Agentic AI app for optimizing Amazon listings using LangChain and LLMs.",
    tech: ["LangChain", "OpenAI API", "Streamlit"],
    link: "#"
  }
];

const Projects = () => {
  return (
    <section id="projects" className="py-20 bg-gray-50 dark:bg-gray-800">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              className="bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg hover:shadow-xl transition"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
            >
              <h3 className="text-xl font-semibold mb-3">{project.title}</h3>
              <p className="text-gray-600 dark:text-gray-300 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.tech.map((tech, i) => (
                  <span
                    key={i}
                    className="bg-blue-100 dark:bg-blue-900/30 text-blue-700 dark:text-blue-300 text-sm px-3 py-1 rounded-full"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <a
                href={project.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 dark:text-blue-400 font-medium hover:underline"
              >
                View Project â†’
              </a>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
