{blogs.map((blog) => (
  <Link
    key={blog.slug}
    to={`/blog/${blog.slug}`}
    className="block p-4 bg-gray-100 dark:bg-gray-800 rounded-xl shadow hover:shadow-lg transition"
  >
    <h2 className="text-xl font-semibold">{blog.title}</h2>  {/* ✅ string */}
    <p className="text-sm text-gray-500">{blog.date}</p>    {/* ✅ string */}
  </Link>
))}
