// src/components/Projects.js
import React, { useState } from 'react';

const projectsData = [
  {
    title: 'Project 1',
    description: 'A short description of project 1.',
    tags: ['ML', 'NLP'],
    github: '#',
    demo: '#',
  },
  {
    title: 'Project 2',
    description: 'A short description of project 2.',
    tags: ['LLM', 'LangChain'],
    github: '#',
    demo: '#',
  },
  {
    title: 'Project 3',
    description: 'A short description of project 3.',
    tags: ['Deep Learning'],
    github: '#',
    demo: '#',
  },
  {
    title: 'Project 4',
    description: 'A short description of project 4.',
    tags: ['Python', 'API'],
    github: '#',
    demo: '#',
  },
  {
    title: 'Project 5',
    description: 'A short description of project 5.',
    tags: ['NLP'],
    github: '#',
    demo: '#',
  },
  {
    title: 'Project 6',
    description: 'A short description of project 6.',
    tags: ['Docker', 'ML'],
    github: '#',
    demo: '#',
  },
];

const allTags = ['All', ...new Set(projectsData.flatMap(p => p.tags))];

const Projects = () => {
  const [filter, setFilter] = useState('All');

  const filteredProjects =
    filter === 'All'
      ? projectsData
      : projectsData.filter(project => project.tags.includes(filter));

  return (
    <section id="projects" className="py-16 bg-gray-50 dark:bg-gray-800">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Projects</h2>

        {/* Tag Filters */}
        <div className="flex flex-wrap justify-center gap-2 mb-8">
          {allTags.map(tag => (
            <button
              key={tag}
              onClick={() => setFilter(tag)}
              className={`px-4 py-2 rounded-full ${
                filter === tag
                  ? 'bg-indigo-600 text-white'
                  : 'bg-gray-200 dark:bg-gray-700'
              }`}
            >
              {tag}
            </button>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProjects.map((project, index) => (
            <div
              key={index}
              className="bg-white dark:bg-gray-700 p-6 rounded-lg shadow-lg hover:shadow-xl transition"
            >
              <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
              <p className="mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.tags.map((tag, i) => (
                  <span
                    key={i}
                    className="text-xs bg-indigo-100 dark:bg-indigo-900 text-indigo-800 dark:text-indigo-200 px-2 py-1 rounded"
                  >
                    {tag}
                  </span>
                ))}
              </div>
              <div className="flex gap-4">
                <a
                  href={project.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-indigo-600 hover:underline"
                >
                  GitHub
                </a>
                {project.demo && (
                  <a
                    href={project.demo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-indigo-600 hover:underline"
                  >
                    Demo
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
