import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import matter from "gray-matter";
import ReactMarkdown from "react-markdown";

function importAll(r) {
  return r.keys().reduce((acc, fileName) => {
    acc[fileName.replace("./", "").replace(".md", "")] = r(fileName);
    return acc;
  }, {});
}

const allPosts = importAll(require.context("../blogs", false, /\.md$/));

export default function BlogPost() {
  const { slug } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    const file = allPosts[slug];
    if (file) {
      const { data, content } = matter(file.default);
      setPost({ ...data, content });
    }
  }, [slug]);

  if (!post) return <p>Blog not found.</p>;

  return (
    <article className="prose dark:prose-invert mx-auto p-6">
      <h1>{post.title}</h1>
      <p className="text-sm text-gray-500">{post.date}</p>
      <ReactMarkdown>{post.content}</ReactMarkdown>
    </article>
  );
}
