// src/components/Hero.js
import React from "react";
import { motion } from "framer-motion";
import { TypeAnimation } from "react-type-animation";

const Hero = () => {
  return (
    <section
      id="hero"
      className="min-h-screen flex flex-col justify-center items-center text-center px-6 bg-gradient-to-b from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800"
    >
      {/* Intro */}
      <motion.h1
        className="text-5xl md:text-6xl font-extrabold text-gray-900 dark:text-white mb-6 leading-tight"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Hi, Iâ€™m <span className="text-blue-600">Farrukh</span>
      </motion.h1>

      {/* Animated Role */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.8 }}
        className="text-2xl md:text-3xl font-semibold text-gray-700 dark:text-gray-300 mb-8"
      >
        <TypeAnimation
          sequence={[
            "Machine Learning Engineer",
            2000,
            "NLP Specialist",
            2000,
            "LLM Fine-Tuning Expert",
            2000,
          ]}
          wrapper="span"
          speed={50}
          repeat={Infinity}
        />
      </motion.div>

      {/* Description */}
      <motion.p
        className="max-w-2xl text-lg md:text-xl text-gray-600 dark:text-gray-400 mb-10"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.8, duration: 0.8 }}
      >
        I design and deploy AI systems that create measurable impact. My focus areas
        include <strong>Large Language Models</strong>, <strong>Natural Language Processing</strong>,
        and <strong>predictive analytics</strong> for business transformation.
      </motion.p>

      {/* CTA Buttons */}
      <motion.div
        className="flex flex-col sm:flex-row gap-4 justify-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1, duration: 0.8 }}
      >
        <a
          href="#projects"
          className="px-8 py-4 bg-blue-600 text-white rounded-xl shadow-lg hover:bg-blue-700 transition text-lg font-medium"
        >
          View My Work
        </a>
        <a
          href="[RESUME_LINK_HERE]"
          target="_blank"
          rel="noopener noreferrer"
          className="px-8 py-4 border border-blue-600 text-blue-600 dark:text-blue-400 rounded-xl hover:bg-blue-50 dark:hover:bg-gray-800 transition text-lg font-medium"
        >
          Download Resume
        </a>
      </motion.div>

      {/* Floating Icons (Optional Visual Flair) */}
      <div className="absolute top-10 right-10 opacity-10 text-8xl text-blue-200 dark:text-gray-600">
  
      </div>
      <div className="absolute bottom-10 left-10 opacity-10 text-8xl text-indigo-200 dark:text-gray-700">

      </div>
    </section>
  );
};

export default Hero;
