// src/components/Hero.js
import React from 'react';

const Hero = () => {
  return (
    <section id="hero" className="pt-24 pb-16 md:pt-32 md:pb-24 bg-gradient-to-r from-indigo-500 to-purple-600 text-white">
      <div className="container mx-auto px-4 text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">[Your Full Name]</h1>
        <p className="text-xl md:text-2xl mb-8">Machine Learning Engineer | NLP | LLM Fine-Tuning</p>
        <a
          href="#projects"
          className="inline-block bg-white text-indigo-600 font-semibold py-3 px-6 rounded-lg hover:bg-gray-100 transition"
        >
          View My Work
        </a>
      </div>
    </section>
  );
};

export default Hero;
