import React from 'react';

const credentials = [
  {
    title: 'Deep Learning Specialization',
    provider: 'Coursera (Andrew Ng)',
    year: '2023',
    link: '#'
  },
  {
    title: 'Natural Language Processing Specialization',
    provider: 'Coursera',
    year: '2023',
    link: '#'
  },
  {
    title: 'Generative AI with Large Language Models',
    provider: 'AWS & DeepLearning.AI',
    year: '2024',
    link: '#'
  },
  {
    title: 'LangChain for LLM Applications',
    provider: 'DeepLearning.AI',
    year: '2024',
    link: '#'
  },
  {
    title: 'MLOps Specialization',
    provider: 'Coursera',
    year: '2023',
    link: '#'
  },
  {
    title: 'Data Science Professional Certificate',
    provider: 'IBM',
    year: '2022',
    link: '#'
  },
  {
    title: 'Advanced Prompt Engineering',
    provider: 'Prompt Engineering Institute',
    year: '2024',
    link: '#'
  },
  {
    title: 'AWS Certified Machine Learning – Specialty',
    provider: 'AWS',
    year: '2024',
    link: '#'
  }
];

const Credentials = ({ isVisible }) => {
  return (
    <section
      id="credentials"
      className={`section-padding bg-gray-50 dark:bg-gray-800 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Credentials</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-12 max-w-3xl mx-auto">
          Verified certifications and professional training in AI, ML, and MLOps
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {credentials.map((cert, index) => (
            <div
              key={index}
              className="bg-white dark:bg-gray-900 rounded-xl shadow-sm hover:shadow-md p-6 transition-all"
            >
              <h3 className="text-lg font-bold text-primary-600 dark:text-primary-400 mb-2">
                {cert.title}
              </h3>
              <p className="text-gray-600 dark:text-gray-400 text-sm mb-2">
                {cert.provider}
              </p>
              <p className="text-xs text-gray-500 dark:text-gray-500 mb-4">
                {cert.year}
              </p>
              {cert.link && (
                <a
                  href={cert.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-primary-600 dark:text-primary-400 font-medium hover:text-accent-500 text-sm"
                >
                  View Credential →
                </a>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Credentials;
