import React from 'react';

const skillCategories = [
  {
    title: 'Core Programming & Data Handling',
    skills: ['Python', 'NumPy', 'Pandas', 'SQL', 'Apache Spark', 'Dask']
  },
  {
    title: 'Machine Learning & Deep Learning',
    skills: ['PyTorch', 'TensorFlow', 'Keras', 'Scikit-learn', 'XGBoost', 'LightGBM', 'OpenCV']
  },
  {
    title: 'LLM & Advanced NLP',
    skills: [
      'LangChain', 'LangGraph', 'Hugging Face', 'Sentence Transformers',
      'Tokenization', 'Embeddings', 'RAG', 'FAISS', 'Chroma', 'Weaviate'
    ]
  },
  {
    title: 'Model Optimization & Deployment',
    skills: [
      'ONNX', 'Knowledge Distillation', 'Pruning', 'Quantization',
      'TensorRT', 'TorchScript', 'FastAPI', 'TorchServe', 'Triton Inference Server'
    ]
  },
  {
    title: 'MLOps & Experiment Tracking',
    skills: [
      'Docker', 'Git', 'Linux', 'Weights & Biases', 'MLflow', 'DVC',
      'Kubeflow', 'CI/CD for ML'
    ]
  },
  {
    title: 'Cloud & Scalable ML',
    skills: [
      'AWS SageMaker', 'AWS EC2', 'AWS S3', 'GCP Vertex AI', 'BigQuery',
      'Azure ML', 'Ray', 'Kubernetes'
    ]
  }
];

const Skills = ({ isVisible }) => {
  return (
    <section
      id="skills"
      className={`section-padding bg-white dark:bg-gray-900 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Technical Skills</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-12 max-w-3xl mx-auto">
          Expertise across Machine Learning, LLMs, Model Optimization, and MLOps
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {skillCategories.map((category, idx) => (
            <div
              key={idx}
              className="bg-gray-50 dark:bg-gray-800 p-6 rounded-xl shadow-sm hover:shadow-md transition-shadow"
            >
              <h3 className="text-lg font-bold text-primary-600 dark:text-primary-400 mb-4">
                {category.title}
              </h3>
              <div className="flex flex-wrap gap-2">
                {category.skills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-primary-100 dark:bg-primary-900/30 text-primary-800 dark:text-primary-200 text-sm rounded-full font-medium hover:bg-primary-200 dark:hover:bg-primary-800 transition-colors"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
