import { Card, CardContent } from "@/components/ui/card"
import { Check, RefreshCcw, Pin } from "lucide-react"

export default function SkillsSection() {
  const skills = [
    {
      category: "TinyML & Machine Learning",
      items: [
        { name: "Fundamentals of Machine Learning", status: "done" },
        { name: "TinyML model deployment on microcontrollers", status: "learning" },
        { name: "Model compression & optimization", status: "pipeline" },
      ],
    },
    {
      category: "Programming & Tools",
      items: [
        { name: "Python for ML workflows", status: "done" },
        { name: "C/C++ for embedded systems", status: "learning" },
        { name: "Rust for resource-constrained AI", status: "pipeline" },
      ],
    },
    {
      category: "Embedded Systems & Hardware",
      items: [
        { name: "Arduino & ESP32 programming", status: "done" },
        { name: "Sensor integration & data acquisition", status: "learning" },
        { name: "Power optimization techniques for edge devices", status: "pipeline" },
      ],
    },
    {
      category: "Mechanical Engineering & Physical Systems",
      items: [
        { name: "Dynamics of physical systems", status: "done" },
        { name: "Combining AI with physical models", status: "learning" },
        { name: "Cross-disciplinary applications in industrial IoT", status: "pipeline" },
      ],
    },
    {
      category: "AI Deployment & Applications",
      items: [
        { name: "Prototyping ML solutions on low-power devices", status: "done" },
        { name: "On-device decision-making & inference", status: "learning" },
        { name: "Secure, private, and offline AI applications", status: "pipeline" },
      ],
    },
  ]

  const statusIcon = (status) => {
    switch (status) {
      case "done":
        return <Check className="text-green-500 w-4 h-4" /> // âœ…
      case "learning":
        return <RefreshCcw className="text-blue-500 w-4 h-4 animate-spin-slow" /> // ðŸ”„
      case "pipeline":
        return <Pin className="text-yellow-500 w-4 h-4" /> // ðŸ“Œ
      default:
        return null
    }
  }

  return (
    <section className="py-10 px-6 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold mb-8 text-center">Skills</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {skills.map((group, idx) => (
          <Card key={idx} className="rounded-2xl shadow-md border border-gray-200">
            <CardContent className="p-6">
              <h3 className="text-xl font-semibold mb-4">{group.category}</h3>
              <ul className="space-y-3">
                {group.items.map((skill, i) => (
                  <li key={i} className="flex items-center gap-2">
                    {statusIcon(skill.status)}
                    <span>{skill.name}</span>
                  </li>
                ))}
              </ul>
            </CardContent>
          </Card>
        ))}
      </div>
    </section>
  )
}
