// src/components/Skills.js
import React from 'react';

const skillCategories = [
  {
    title: 'Programming & ML Frameworks',
    skills: [
      'Python',
      'PyTorch',
      'TensorFlow',
      'Scikit-learn',
      'OpenCV',
      'NumPy',
      'Pandas'
    ]
  },
  {
    title: 'MLOps & Infrastructure',
    skills: [
      'Docker',
      'Linux',
      'AWS',
      'Git',
      'SQL',
      'Weights & Biases'
    ]
  },
  {
    title: 'Advanced Techniques',
    skills: [
      'LangChain',
      'LangGraph',
      'ONNX',
      'Knowledge Distillation',
      'Pruning',
      'Quantization'
    ]
  }
];

const Skills = ({ isVisible }) => {
  return (
    <section
      id="skills"
      className={`section-padding bg-white dark:bg-gray-900 transition-all duration-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
      }`}
    >
      <div className="container mx-auto px-4">
        <h2 className="section-title">Technical Skills</h2>
        <p className="text-center text-gray-600 dark:text-gray-300 mb-12 max-w-3xl mx-auto">
          Expertise across Machine Learning, MLOps, and Advanced AI techniques
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          {skillCategories.map((category, idx) => (
            <div key={idx} className="bg-gray-50 dark:bg-gray-800 p-6 rounded-xl shadow-md">
              <h3 className="text-lg font-bold text-primary-600 dark:text-primary-400 mb-4">
                {category.title}
              </h3>
              <div className="flex flex-wrap gap-3">
                {category.skills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-4 py-1 bg-primary-100 dark:bg-primary-900/30 text-primary-800 dark:text-primary-200 text-sm rounded-full font-medium"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
