import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import matter from "gray-matter";

function importAll(r) {
  return r.keys().map((fileName) => {
    const file = r(fileName);
    const slug = fileName.replace("./", "").replace(".md", "");
    return { slug, ...file };
  });
}

export default function BlogList() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const mdFiles = importAll(require.context("../blogs", false, /\.md$/));
    const parsed = mdFiles.map((mod) => {
      const content = mod.default;
      const { data } = matter(content);
      return { ...data, slug: data.slug };
    });
    setBlogs(parsed);
  }, []);

  return (
    <section className="p-6">
      <h1 className="text-3xl font-bold mb-6">Blog</h1>
      <div className="grid gap-4">
        {blogs.map((blog) => (
          <Link
            key={blog.slug}
            to={`/blog/${blog.slug}`}
            className="block p-4 bg-gray-100 dark:bg-gray-800 rounded-xl shadow hover:shadow-lg transition"
          >
            <h2 className="text-xl font-semibold">{blog.title}</h2>
            <p className="text-sm text-gray-500">{blog.date}</p>
          </Link>
        ))}
      </div>
    </section>
  );
}
